const n=document.querySelector(".menu-lista"),c=document.querySelector(".icono");function s(){c.classList.toggle("fa-xmark"),n.classList.toggle("lista-visible")}c.addEventListener("click",s);document.addEventListener("DOMContentLoaded",function(){const o=location.pathname;document.querySelectorAll(".menu-link").forEach(e=>{e.getAttribute("href")===o&&e.classList.add("text-emerald-600")})});document.addEventListener("DOMContentLoaded",function(){const o=location.pathname;document.querySelectorAll(".link").forEach(e=>{e.getAttribute("href")===o&&e.classList.add("text-emerald-400","font-bold")})});const t=document.querySelector(".aviso-contenedor"),r=document.querySelector(".aceptar-btn"),l=document.querySelector(".rechazar-btn");localStorage.getItem("cookies-aceptadas")?t.classList.remove("activo"):t.classList.add("activo");r.addEventListener("click",()=>{t.classList.remove("activo"),localStorage.setItem("cookies-aceptadas","si")});l.addEventListener("click",()=>{t.classList.remove("activo"),localStorage.setItem("cookies-aceptadas","no")});
