import"https://kit.fontawesome.com/a7a0089e5e.js";const n=document.querySelector(".menu-lista"),c=document.querySelector(".menu-icono");function s(){c.classList.toggle("fa-xmark"),n.classList.toggle("lista-visible")}c.addEventListener("click",s);document.addEventListener("DOMContentLoaded",function(){const o=location.pathname;document.querySelectorAll(".menu-link").forEach(e=>{e.getAttribute("href")===o&&e.classList.add("activo")})});document.addEventListener("DOMContentLoaded",function(){const o=location.pathname;document.querySelectorAll(".footer-menu-link").forEach(e=>{e.getAttribute("href")===o&&e.classList.add("activo")})});const t=document.querySelector(".aviso-contenedor"),i=document.querySelector(".aceptar-btn"),r=document.querySelector(".rechazar-btn");localStorage.getItem("cookies-aceptadas")?t.classList.remove("activo"):t.classList.add("activo");i.addEventListener("click",()=>{t.classList.remove("activo"),localStorage.setItem("cookies-aceptadas","si")});r.addEventListener("click",()=>{t.classList.remove("activo"),localStorage.setItem("cookies-aceptadas","no")});
